(function () {
	let stew;

	if (typeof window === 'object') {
		stew = window.stew;
	} else {
		stew = require('@triplett/stew');
	}

	function Hello ({ sentiment, region }) {
		const state = stew.createState({ isAlternate: false });

		return () => ['', null,
			['p', null, state.isAlternate ? 'Goodnight Moon' : `${sentiment} ${region}`],
			['button', { type: 'button', onclick: () => state.isAlternate = !state.isAlternate }, 'Respond'],
		];
	}

	if (typeof window === 'object') {
		window.components.hello = Hello;
	} else {
		module.exports = Hello;
	}
})();
